        [283] = {
        ["Title"] = "泰兰德的残影",
        ["Description"] = "作为前暗夜精灵领袖及艾露恩高阶祭司，这个位于错乱时空中的泰兰德·语风的残影在艾泽拉斯未来的废土上徘徊。她被永恒的黑夜包围，几乎看不清艾露恩那令人慰藉的光辉。",
        },
        [693] = {
        ["Title"] = "宰相金巴卡",
        ["Description"] = "宰相金巴卡就在螳螂妖的天然桥梁附近游荡，收集树根分泌出的宝贵汁液。利用自己掌握的广博炼金学识，这个强大的家伙会将这种物质转变为武器和其它工具，帮助推进对砮皂寺的围攻。",
        },
        [411] = {
        ["Title"] = "卫兵摩尔达",
        ["Description"] = "摩尔达是戈多克食人魔中的狂热忠诚守卫。他说服戈多克大王赏给他一副施了魔法的手套，使他在族人间连续六年荣膺掰腕子大赛的冠军。事实上，戈多克大王是把这强大的护甲丢弃了，摩尔达只不过是第一个捡到它的人而已。",
        },
        [475] = {
        ["Title"] = "考布莱恩",
        ["Description"] = "加拉克斯拉驾驭动物形态的能力，为他在塞纳里奥议会的德鲁伊间赢得了威名。然而，哀嚎洞穴的腐化却扭曲了他的特殊能力。现在，加拉克斯拉已将一种致命的毒蛇形态演练得出神入化，为自己冠以考布莱恩的新名字，以体现他那全新的邪恶力量。",
        },
        [566] = {
        ["Title"] = "高阶术士奈瑟库斯",
        ["Description"] = "奈瑟库斯曾是影月氏族的萨满祭司，他放弃了对元素的崇敬，转而研习运用邪恶魔法的艺术。他所控制的恶魔能量使他仅剩下一副躯壳，唯有在折磨年轻的邪兽人时才能获得乐趣，以检验他们精神和肉体力量的极限。",
        },
        [174] = {
        ["Title"] = "奈法利安的末日",
        ["Description"] = "因为曾使用艾泽拉斯各大龙族军团的精华来创造多彩巨龙，死亡之翼之子奈法利安变得臭名昭著。在他死于黑翼之巢后，死亡之翼将他复活，并赐予其新的力量，让他继续那邪恶的计划。",
        },
        [881] = {
        ["Title"] = "迦拉卡斯",
        ["Description"] = "督军扎伊拉和加尔鲁什在暮光高地的事件期间形成了非常亲密的关系，且她和她的龙喉兽人承诺为加尔鲁什的事业效忠。骇人的迦拉卡斯是所有龙族灾变祖先的直系后裔，扎伊拉骑在它的背上，监管着奥格瑞玛的海防力量。",
        },
        [695] = {
        ["Title"] = "黑暗萨满柯兰萨",
        ["Description"] = "柯兰萨始终认为凶蛮之力是控制艾泽拉斯元素的唯一方法。正是这一极端观点使他加入了邪恶的暮光之锤。当教派衰落后，这位兽人和其它志同道合的萨满一同潜入了怒焰裂谷，继续从事他们的邪恶勾当。",
        },
        [412] = {
        ["Title"] = "践踏者克雷格",
        ["Description"] = "鬼祟的刺客们不止一次因打翻克雷格的酒杯而惨遭毒手。尽管他总是会在当值时喝得酩酊大醉，但是只要一听到杯中美酒洒在石头地面上的声音，这位食人魔就会立即醒来，勃然大怒。",
        },
        [476] = {
        ["Title"] = "皮萨斯",
        ["Description"] = "作为纳拉雷克斯的儿时玩伴，战士阿里恩在德鲁伊之道上有着过人的天赋。尽管受过严格训练，但这位侍僧却仍无力对抗令他丧失理智的腐化。阿里恩改名换姓，以皮萨斯的新身份将自己超凡的近战能力与德鲁伊的学识合二为一，粉碎一切入侵者。",
        },
        [568] = {
        ["Title"] = "战争使者沃姆罗格",
        ["Description"] = "凶残而狡诈的食人魔沃姆罗格被视作是邪部落最致命的成员之一。但这一人人惧怕的暴徒却也有自己的麻烦。这位食人魔颈上的双颅总会在哪怕是最简单的事情上争论不休。两年前，沃姆罗格开始了一个由十二个步骤组成的计划，从而让他好战的双重性格取得同步。但他的双颅至今还没决定好先从哪一个步骤开始实行。",
        },
        [696] = {
        ["Title"] = "焰喉",
        ["Description"] = "事实证明，与其它被黑暗萨满奴役的熔岩虫不同，焰喉几乎不受任何控制。这只猛兽在地下深处恣意钻掘，对其行动引发的后果毫不在意，谁要是挡了它的路也只能自认倒霉。",
        },
        [824] = {
        ["Title"] = "黑暗意志",
        ["Description"] = "雷神在统治期间不遗余力地塑造机械仆从，令其毫无畏惧地戍守要塞。在首次尝试以失败告终后，雷电之王变得怒不可遏。他将自己的负面情绪倾注在下一个——也是他最成功的造物身上，那就是黑暗意志。直到今天，这个华丽的构造体仍在尽忠职守，汇聚魔古生命改造实验精华的神秘物质使其变得无比强大。",
        },
        [614] = {
        ["Title"] = "玛尔加尼斯",
        ["Description"] = "玛尔加尼斯在历史中的地位已经定格了。阿尔萨斯对这名恐惧魔王的仇恨将驱使他前往诺森德，拿起霜之哀伤。但这一切的前提是玛尔加尼斯在战斗中认同阿尔萨斯的价值。为了证明自己的价值，踏上成为巫妖王的征途，阿尔萨斯必须在玛尔加尼斯的攻势下存活下来。",
        },
        [285] = {
        ["Title"] = "吉安娜的残影",
        ["Description"] = "吉安娜·普罗德摩尔饱受折磨的投影已经惨遭撕裂并被注入了她的法杖碎片中。为了恢复时间流的平衡，你必须击败这个残影。可是，这位骄傲的前塞拉摩统治者掌握着毁灭性的魔法力量，而她这个时空错乱的幽灵投影同样如此。在这个未来的艾泽拉斯，她只是被分裂了，并且等待着……",
        },
        [697] = {
        ["Title"] = "熔岩守卫戈多斯",
        ["Description"] = "在狂妄的驱使下，才华横溢的萨满戈多斯变成了汲取火焰之地力量的活体导管。他和同伴想要效仿暮光之锤创造的元素升腾者。结果，能量汇聚而引发的爆炸使戈多斯陷入疯狂，还把他的身体扭曲成了令人惨不忍睹的形态。",
        },
        [413] = {
        ["Title"] = "卫兵芬古斯",
        ["Description"] = "自从观察者克鲁什告诉他，说他能在执勤时遇见命中注定的伴侣，芬古斯便时刻打起十二万分精神在戈多克领土上巡逻。",
        },
        [477] = {
        ["Title"] = "克雷什",
        ["Description"] = "这只巨龟是哀嚎洞穴的古老住民，终日在地下洞穴间游荡，他顽强的心智丝毫没有受到纳拉雷克斯无情噩梦的影响。别被他乐天的性情给骗了，克雷什已经将许多妄图偷取他巨大龟壳的冒险者打得落荒而逃。",
        },
        [570] = {
        ["Title"] = "背叛者门努",
        ["Description"] = "门努原本是一位破碎者，是被兽人释放出的能量扭曲变形的德莱尼。当纳迦入侵赞加沼泽时，这些不幸的生物有许多都试图逃离这一地区。门努却另辟了一条通往幸福的蹊径。为了活命，他背叛了自己的破碎者同伴，转而为这些蛇形入侵者效命，同意以奴隶监工的身份看管自己的族人。",
        },
        [175] = {
        ["Title"] = "高阶祭司温诺希斯",
        ["Description"] = "温诺希斯本来是被派去杀死哈卡的，可他却被那凶残的存在所奴役，直到艾泽拉斯的英雄们完成了他的使命。在夺灵者的使徒们的努力下，温诺希斯从灵魂世界转生，受命和他的毒蛇一起守卫祖尔格拉布。",
        },
        [826] = {
        ["Title"] = "乌达斯塔",
        ["Description"] = "随着对雷神岛堡垒的战役打响，联盟和部落的斥候就一直致力于截断几个主要的赞达拉营地间的往来通信。其中一封似乎围绕着一个未知的赞达拉语——乌达斯塔，而且总是和力量还有征服挂上钩。在潘达利亚的某处，赞达拉的驭兽者将他们的强大野兽作为战争机器来驯养，且这种被神灵之力所灌注的怪物已接近完成阶段。至于具体的地点位置，尚未知晓……",
        },
        [869] = {
        ["Title"] = "加尔鲁什·地狱咆哮",
        ["Description"] = "当萨尔在外域遇见这位年轻的兽人时，格罗玛什·地狱咆哮之子——加尔鲁什第一次听说了他父亲的英雄事迹。骄傲的种子在他心中生根发芽。加尔鲁什作为酋长，率领部落大军在诺森德频频告捷，在大灾变的混乱中巩固了部落的力量。但他不择手段打造兽人霸权的理想，已经引得世界各路大军一路杀向奥格瑞玛……要向加尔鲁什发起最终清算，而他本人也已经做好了血战到底的准备。",
        },
        [571] = {
        ["Title"] = "巨钳鲁克玛尔",
        ["Description"] = "在德拉诺覆灭后，鲁克玛尔逃进了赞加沼泽深处。撕裂世界的能量席卷了这只巨兽，它的力量和体型都变得更大，但却要付出忍受无尽痛苦的代价。当纳迦初次来到这片区域时，他们为鲁克玛尔的凶猛而感到震惊不已。纳迦没有杀死这只野兽，而是奴役了它，用这只生物来管制赞加沼泽众多的本土生物。",
        },
        [577] = {
        ["Title"] = "加兹安",
        ["Description"] = "历史学家长久以来都对艾泽拉斯与外域的巨型多头蛇是否同出一源争论不休。但有一点可以确定的是，这些凶残的生物从上古时期开始就在这两块区域存在了。加兹安是外域最为恶名昭彰、可怕骇人的多头蛇。在瓦斯琪女士的控制下，这只贪婪的恶魔对赞加沼泽的下层发起了猛攻，以威胁当地的住民。",
        },
        [414] = {
        ["Title"] = "卫兵斯里基克",
        ["Description"] = "有些人也许会认为斯里基克只是小心谨慎，可他其实也懒得很。他不愿与戈多克大王正面交锋，而是希望凭借陷阱和又老又破的魔法装置，不费吹灰之力地对其造成致命打击。",
        },
        [478] = {
        ["Title"] = "斯卡姆",
        ["Description"] = "斯卡姆原本只是为了逃避贫瘠之地的恶劣环境才前往哀嚎洞穴的。与洞内的许多其它住民一样，他也很快就遭到腐化。如今在他周身流淌着黑暗能量，他已经无可挽回地变成了一只强大凶残的野兽。",
        },
        [572] = {
        ["Title"] = "夸格米拉",
        ["Description"] = "夸格米拉曾是赞加沼泽众多真菌巨人的统治者。他在赞加沼泽蜿蜒洞穴中游荡时被纳迦擒获，并遭受了难以忍受的折磨。现在这一饱受摧残的生物心无旁骛地效忠于自己的领主，让所有胆敢违抗瓦丝琪女士意志的人收声。",
        },
        [867] = {
        ["Title"] = "傲之煞",
        ["Description"] = "第七煞叫做傲之煞，是皇帝少昊难以摆脱的最后一项罪责，在它的影响下，皇帝用迷雾遮蔽了这片土地。一万年来，傲之煞一直在等待时机。当加尔鲁什唤醒亚煞极之心时，他心中的傲慢之力使得这股黑暗能量在亚煞极之心出现的房间内聚集了起来。",
        },
        [828] = {
        ["Title"] = "季鹍",
        ["Description"] = "魔古族的血肉塑造者们将失败的造物丢弃在雷神要塞下方的深井里。许多食腐鸟循着腐肉的恶臭来到了这个废料坑，这其中就包括巨禽季鹍。这只巨鸟凶残狡诈，将对手一个接一个地吞入腹中，直到自己成为这幽暗隧道的唯一统治者。",
        },
        [122] = {
        ["Title"] = "希亚玛特",
        ["Description"] = "净化尼斐塞特托维尔人的血肉诅咒，让可怕的元素希亚玛特几乎耗尽了能量。正是在这个薄弱的关头，托维尔人发动了进攻，奇迹般地将希亚玛特囚禁在了他们的沙漠古城的中央。",
        },
        [573] = {
        ["Title"] = "水术师瑟丝比娅",
        ["Description"] = "在暗夜精灵帝国的鼎盛时期，上层精灵瑟斯比娅热衷于艺术与音乐。然而她对这些事物的喜爱在大分裂期间全都消失了，她和众多族人一起被吸进了艾泽拉斯的深渊之中，变成了充满仇恨的纳迦。在那之后的千年里，瑟斯比娅始终是她儿时玩伴瓦丝琪女士的知己密友。她毫不犹豫地与同伴前往赞加沼泽，立誓监督纳迦对该地水源补给的汲取任务。",
        },
        [865] = {
        ["Title"] = "攻城匠师黑索",
        ["Description"] = "赫利克斯·黑索是在加尔鲁什为正统部落寻找工程师的过程中脱颖而出的，他是唯一一位集高超的工程技术、专业性和残忍品质于一身的地精。黑索有着一颗雇佣兵的心，他对自己的创造物（以及高昂报酬）的热爱使他的命运永远地和自己的酋长兼雇主紧紧连在了一起。",
        },
        [415] = {
        ["Title"] = "克罗卡斯",
        ["Description"] = "即便是在食人魔当中，克罗卡斯的火爆脾气也是臭名昭著，谁要是胆敢冒犯他或戈多克大王，就绝对没有好果子吃。他凶残地镇压那些与他意见相左的人，不过，总有人乐此不疲地散播他早年与一只豺狼人的热恋绯闻，对于这类造谣生事者，他更没有耐心。",
        },
        [479] = {
        ["Title"] = "瑟芬迪斯",
        ["Description"] = "尖牙德鲁伊的领袖曾是纳拉雷克斯最出色的学生。他的悲剧源自于他想要成为大导师，也就是“荣誉导师”的热望，想要和纳拉雷克斯比肩。疯狂在他心中生根发芽，瑟芬迪斯将他的兄弟们召集在一起，成立了这个名叫尖牙德鲁伊的组织。",
        },
        [574] = {
        ["Title"] = "机械师斯蒂里格",
        ["Description"] = "诺莫瑞根的辐射物把这座城市中的许多幸存者变成了麻风侏儒。杰出的机械师斯蒂里格就是这些受害者当中的一员。尽管他逃出了诺莫瑞根，并保留住了自己的聪明才智，但他曾经拥有的伦理观和同情心却已尽皆泯灭。他在艾泽拉斯四处游荡，将自己的知识出卖给一些厚颜无耻的组织，如血帆海盗。最近，纳迦雇用了斯蒂里格，让他负责操作赞加沼泽蒸汽地窟里的复杂机械装置。",
        },
        [176] = {
        ["Title"] = "血领主曼多基尔",
        ["Description"] = "恶名昭著的血领主曼多基尔多年前备受折磨而死，之后其尸体更是被血顶巨魔所亵渎。风传曼多基尔将其恶毒的灵魂寄宿在强大的血顶酋长甘祖拉恩的尸体里，血领主再一次出现在了荆棘谷的丛林中。",
        },
        [109] = {
        ["Title"] = "升腾者领主奥西迪斯",
        ["Description"] = "堕落者德希里克曾是一名强大的萨满，因为其狂热的虔诚和对暴行的嗜好，他被恩准升腾：通过一道黑暗仪式，他被注入了元素之力，变成了一头名为升腾者领主奥西迪斯的畸形生物。",
        },
        [861] = {
        ["Title"] = "野牛人火神斡耳朵斯",
        ["Description"] = "斡耳朵斯曾经是一名野牛人萨满祭司，但在火焰之王许下承诺之后，他便将自己献给了烈焰，以期成为一个半神。他的肉身永远被火焰烧灼，这使他陷入了无尽的痛苦之中。野牛人控火者们仪式性地将自己点燃，以效仿斡耳朵斯的成神过程，并通过施法和祈祷的方式来减轻他的痛苦。",
        },
        [575] = {
        ["Title"] = "督军卡利瑟里斯",
        ["Description"] = "在蒸汽地窟深处，纳迦开始用一种神秘物质进行实验，以强化他们的战士，荡除他们心中的恐惧。卡利瑟里斯是瓦斯琪女士的仆从之一，自愿充当这一黑暗药剂的试验品。作为奖赏，他被赋予守卫广阔蒸汽地窟的使命，确保这片区域的奥术机械设备能不受干扰的正常运行。",
        },
        [105] = {
        ["Title"] = "摧骨者罗姆欧格",
        ["Description"] = "作为外域食人魔中少见的“缺失的一环”，摧骨者罗姆欧格因其食人魔凶残的本性而被暮光之锤邪教所看重。他如今统领着焚烧之厅，靠折磨他人满足着自己的嗜虐欲。",
        },
        [416] = {
        ["Title"] = "观察者克鲁什",
        ["Description"] = "克鲁什并没有既定立场，但当乌尔洛克掌权后，他的力量显然已经变得不可动摇。国王很快便任命克鲁什担任他的首席顾问，相信他的双头一定能带来令普通食人魔望尘莫及的双倍睿智。",
        },
        [480] = {
        ["Title"] = "永生者沃尔丹",
        ["Description"] = "在纳拉雷克斯来到哀嚎洞穴之前，人们对这只生物了解甚少。永生者沃尔丹被看作是洞穴的原始守护者，没能逃过降临在自己家园的腐化。沃尔丹与其他疯狂的德鲁伊结为盟友，对那些擅自闯入的不速之客保持着高度警惕。",
        },
        [576] = {
        ["Title"] = "霍加尔芬",
        ["Description"] = "霍加尔芬与同伴夸格米拉不同，这一伟大的真菌巨人目前还没有沦为纳迦的奴隶。然而他始终对所有闯入赞加沼泽至深洞穴的外来者保持着极强的攻击性。霍加尔芬对纳迦在赞加沼泽其它地区实行的阴谋诡计一无所知，花费大量时间照料幽暗孢子，那是一种古老的植物，据说是他最为珍视的宝物。",
        },
        [859] = {
        ["Title"] = "玄牛砮皂",
        ["Description"] = "砮皂发起的是勇气与坚毅的试炼。当皇帝少昊被恐惧席卷时，玄牛劝他直面自己心中的恐惧。皇帝与惧之煞展开了一场旷日持久的战斗，最后终于得胜而还。要想通过玄牛的试炼，战士们必须不屈不挠地面对压倒一切的危险。",
        },
        [832] = {
        ["Title"] = "雷神",
        ["Description"] = "暴君、独裁者、征服者以及皇帝。雷电之王的这些称号之下映照出了潘达利亚其他种族的种种不幸。如今复活于一个魔古族不再拥有统治权的世界里，雷神已经下定决心要结束这出闹剧，并把世界再次踩于他的脚下。",
        },
        [663] = {
        ["Title"] = "詹迪斯·巴罗夫",
        ["Description"] = "课程：高级幻象\r\n\r\n巴罗夫的灵魂已在通灵学院中游荡多年，现在她正发挥出更为积极的作用——训练新生代法师。她掌握着有关幻象的广博知识，对学生而言，她是一位杰出的导师；对那些激怒她的蠢货而言，则是一位可怕的对手。",
        },
        [289] = {
        ["Title"] = "姆诺兹多",
        ["Description"] = "游离于时间流之外，如今的姆诺兹多正是曾经崇高的时光之王，守护巨龙诺兹多姆。由于泰坦让诺兹多姆亲眼目睹了自己的死亡，饱受折磨的巨龙被上古之神所蒙骗。他企图扭转自己的宿命。于是，诺兹多姆破坏了时间的正常流转，并创造出了永恒之龙…艾泽拉斯的未来将被摧毁。",
        },
        [857] = {
        ["Title"] = "朱鹤赤精",
        ["Description"] = "赤精发起的是希望试炼。由于自己族人的未来充满未知数，皇帝少昊请求伟大朱鹤的帮助。朱鹤向少昊提出了挑战，让他在自己的身体里寻找希望，从而抛开疑虑。当疑之煞被战胜后，皇帝变成了自己族人的希望信标。要想通过朱鹤的试炼，战士们必须坚信希望犹存，哪怕是在最黑暗的时期里。",
        },
        [417] = {
        ["Title"] = "戈多克大王",
        ["Description"] = "谁要是想统治戈多克食人魔，只须站出来自封为王……然后消灭所有心怀异议的人即可。戈多克大王——当时人称乌尔洛克——就是劝服了十二位族中最为强大的战士，同时挑战国王，以此巩固自己的力量。一场疯狂混战过后，比试者死的死伤的伤，乌尔洛克手起刀落，不费吹灰之力就结果了那些幸存者和被战斗搞得疲累交加的国王。时至今日，每当戈多克食人魔提及“血战之日”时，还是只敢低声窃语。",
        },
        [481] = {
        ["Title"] = "吞噬者穆坦努斯",
        ["Description"] = "从纳拉雷克斯扭曲心灵最为阴暗的角落里，席卷翡翠梦境的腐化之力已体现在德鲁伊的死敌——鱼人的身上。名叫穆坦努斯的怪物来到这个清醒的世界上，吞噬任何想要将纳拉雷克斯从梦魇中解救出来的人。",
        },
        [578] = {
        ["Title"] = "沼地领主穆塞雷克",
        ["Description"] = "身为破碎者就要接受被放逐的命运。这一真理是沼地领主穆塞雷克的心头大石。虽然他是暗血破碎者的成员，他更愿意独自在赞加沼泽下方的洞穴中游荡。在他最近一次游荡的时候，他利用自己对野兽的驾驭力迷住了唤风者克劳恩，那是一位德鲁伊，当时正以凶猛的熊形态探索那片区域。穆塞雷克强迫克劳恩充当他的忠实同伴，不会因为他被扭曲成了怪物就对他心存偏见。",
        },
        [177] = {
        ["Title"] = "疯狂之缘——格里雷克",
        ["Description"] = "疯狂之缘里有四名充满仇恨的灵魂：可怕的巨魔格里雷克被神剥夺了再生能力；战士雷纳塔基以热衷施虐而臭名昭著；先知哈扎拉尔整日被恐怖的未来预言所折磨；强大的战将乌苏雷则拥有号令风暴的力量。",
        },
        [834] = {
        ["Title"] = "总冠军",
        ["Description"] = "巫妖王凶残至极，他的亡灵大军总是倾巢而出，瞬间席卷他的敌人。为了挑选足以对抗他的后备勇士，大领主提里奥·弗丁发起了勇气、忍耐力和团队协作力的试炼。他麾下经验丰富的精兵强将会淘汰掉那些缺乏勇气，无法面对铺天盖地亡灵大军的弱者。",
        },
        [674] = {
        ["Title"] = "大检察官怀特迈恩",
        ["Description"] = "萨莉·怀特迈恩在童年时期就目睹了可怕的一幕，她的家人在途经北洛丹伦时，感染了可怕的亡灵瘟疫。当他们变成了无脑天灾爪牙时，她被迫亲手杀死了双亲和弟妹，心中充满了内疚与愤怒。从那天起，只有一件事能令这位可怕的祭司感到快乐和满足——那就是消灭亡灵。",
        },
        [290] = {
        ["Title"] = "佩罗萨恩",
        ["Description"] = "与他的许多上层精灵同伴一样，佩罗萨恩立誓效忠于堕落的萨维斯以换取恶魔的力量。\r\n\r\n佩罗萨恩在受到了萨特诅咒的影响后，成了一名操控恶魔法术的大师。他驻守在艾萨拉的宫廷之外，为燃烧军团担当防御职责，时刻准备着消灭那些企图阻止堕落泰坦萨格拉斯入侵艾泽拉斯的冒险者们。",
        },
        [853] = {
        ["Title"] = "卡拉克西英杰",
        ["Description"] = "这九位依然健在的卡拉克西瓦英杰是螳螂妖族群中的古老斗士，曾与唤醒者并肩作战，对抗女皇夏柯希尔的疯狂暴政。但这些英杰与其他所有螳螂妖一样，有着赤诚的拳拳之心。当加尔鲁什找到亚煞极之心时，英杰们追随着自己远古主人的低声呼唤，来到了奥格瑞玛下方的钢铁大厅。",
        },
        [418] = {
        ["Title"] = "群体打击者9-60",
        ["Description"] = "在诺莫瑞根的全盛时期，发明家们会相互比试自己的发明，以此提升自己的地位。工程师奥格索普·奥布诺提斯研制出了一个无敌机器人，这为他在大工匠协会中赢得了尊重。希科·瑟玛普拉格盗取了奥布诺提斯的蓝图，然后造出了一个巨版机器人。他任由自己的伪造品肆虐，引发了严重的后果，无辜的奥布诺提斯因此遭到了管理委员会的驱逐。群体打击者9-60至今仍是瑟玛普拉格最倾心的（剽窃）发明之一。",
        },
        [482] = {
        ["Title"] = "水占师维蕾萨",
        ["Description"] = "与大多数沙怒巨魔不同，水占师维蕾萨已经游遍世界各地，收集知识和神器，来帮她控制祖尔法拉克的圣水。尽管水占师的行为使她在异国他乡获敌无数，她却将驾驭这惊人力量的能力视作对自己往昔劣迹的弥补。",
        },
        [580] = {
        ["Title"] = "纳多克斯长老",
        ["Description"] = "纳多克斯原本是一位倍受尊敬的蛛魔领袖，将他种族的古老传统转授给后世子嗣。当蜘蛛之战席卷艾卓-尼鲁布时，他被亡灵军团击败了。巫妖王复活了纳多克斯的尸体，将他体内一切睿智高尚的品质悉数荡除，迫使他杀死了自己的学生、好友和所有其它对抗天灾之力的蛛魔。",
        },
        [708] = {
        ["Title"] = "君王的试炼",
        ["Description"] = "鬼谋神算冥谷子、力冠三军库乌艾和势不可挡哈伊岩携各自的部族来到了宫殿，争相向他们的君王证明自己的价值。可这些魔古族派系却遭遇了可怕的对手。暴力威胁要席卷这座宏伟的殿堂，除非这几个水火不容的部族能找到无需大动干戈即可证明其力量的方法。",
        },
        [709] = {
        ["Title"] = "惧之煞",
        ["Description"] = "惧之煞是潘达利亚恐惧的实体形态。很久以前被传说中的熊猫人皇帝少昊囚禁，近来却因岛上纷争四起而再次变得强大。",
        },
        [851] = {
        ["Title"] = "嗜血的索克",
        ["Description"] = "当巨兽岛在潘达利亚沿海被发现时，岛上住满了原始恐龙，加尔鲁什派人抓了一些最为可怕的恐龙回来做样本，希望能够驾驭它们，把它们变为战争巨兽。无数想要驯服索克的兽人驯兽师都倒在了它的巨颚之下，但这头巨兽对鲜血的渴望却始终得不到满足。",
        },
        [291] = {
        ["Title"] = "艾萨拉女王",
        ["Description"] = "歌颂艾萨拉女王的美丽、威严与强大的歌谣数不胜数，可也正是她的虚荣导致上层精灵召来了燃烧军团。现在，她正坐视她最信赖的法师们为萨格拉斯降临艾泽拉斯的传送门输送能量，让她的众多子民在黑暗泰坦降临之前死去。",
        },
        [89] = {
        ["Title"] = "格拉布托克",
        ["Description"] = "只是一阵心血来潮，格拉布托克就释放出强大的魔法力量，把他的食人魔山给夷为了平地。听到这个消息，迪菲亚立刻雇佣了这只硕大的食人魔法师作为死亡矿井的工头，借助他毁灭性的天赋来监督那些劳工。",
        },
        [419] = {
        ["Title"] = "格鲁比斯",
        ["Description"] = "在城内弥漫的放射性气体本是为了激怒穴居人，杀死侏儒，而瑟玛普拉格的凶残计划也实施得天衣无缝。但有一只穴居人却对这种物质产生了奇怪的反应——格鲁比斯长啊，长啊，越长越大。这是令希科始料未及的副作用，可他从来都不是一个会浪费珍贵（且危险）资源的人。这只体型庞大的穴居人如今就在诺莫瑞根的隧道中巡视。他忠实的蜥蜴宠物咀嚼者则帮助格鲁比斯嗅探气体泄漏，寻找美味的真菌，甚至是可口的入侵者。",
        },
        [483] = {
        ["Title"] = "加兹瑞拉",
        ["Description"] = "相传，加兹瑞拉是上古之神心爱的宠物，甚至她自身就是一位半神。不论真相如何，沙怒巨魔都对这只多头蛇有着长达数千年的崇拜历史。由于对这野兽的寒冰怒火心怀惧意，巨魔们只会在祭品丰裕时才会唤醒加兹瑞拉，让它大快朵颐。",
        },
        [582] = {
        ["Title"] = "耶戈达·觅影者",
        ["Description"] = "耶戈达梦到了天启。她相传是暮光之锤最为狂热的追随者之一。在凶残食人魔法师古加尔的指引下，她学会了扭曲自己的萨满之力以效忠上古之神。不久以前，耶戈达进入了安卡赫特深处，准备迎接光荣日的到来——届时尤格-萨隆将再次崛起，用暗影吞噬艾泽拉斯的所有土地。",
        },
        [178] = {
        ["Title"] = "疯狂之缘——哈扎拉尔",
        ["Description"] = "疯狂之缘里有四名充满仇恨的灵魂：可怕的巨魔格里雷克被神剥夺了再生能力；战士雷纳塔基以热衷施虐而臭名昭著；先知哈扎拉尔整日被恐怖的未来预言所折磨；强大的战将乌苏雷则拥有号令风暴的力量。",
        },
        [669] = {
        ["Title"] = "跳跳大王",
        ["Description"] = "跳跳大王是四风谷农夫的天敌——他是一只庞大无比的兔妖，一晚上就能吃掉整片农田的庄稼。有传言称，这只大害虫和他的儿孙们已经侵入酿酒厂，开始吞吃存放在那里的精米和大麦。",
        },
        [849] = {
        ["Title"] = "堕落的守护者",
        ["Description"] = "那场毁灭性的爆炸把锦绣谷变得面目全非，身处这场灾难正中心的金莲教和影踪派的守卫者们也被黑暗能量撕得粉碎。他们的灵魂仍然徘徊在自己生前守护着的地方，为自己的无能而感到困惑和痛心。",
        },
        [292] = {
        ["Title"] = "玛诺洛斯与瓦罗森",
        ["Description"] = "瓦罗森是艾萨拉女王的亲卫队长，而玛诺洛斯则是在未来用自己的血液使所有兽人陷入堕落的深渊领主。现在他们组成了最后一道防线，抵御任何企图阻止萨格拉斯进入艾泽拉斯的势力。尽管泰兰德和伊利丹奋战至此，但永恒之井的动荡不安已经将艾泽拉斯的命运推向了危机的边缘。",
        },
        [113] = {
        ["Title"] = "高阶女祭司艾苏尔",
        ["Description"] = "尽管心智尽毁，但高阶女祭司艾苏尔仍然是少数几个能在同暮光之锤教派恐怖的主子沟通后，还能活下来的人。她将恐怖植根在低级教徒心中，好让他们在主子下令的时候，毫不犹豫地牺牲自己。",
        },
        [420] = {
        ["Title"] = "粘性辐射尘",
        ["Description"] = "诺莫瑞根那原本恢弘优雅的复杂水道，现在被辐射得乌烟瘴气，到处都是毒性泥浆怪。有个曾住在侏儒蓄水层中的孤单水元素被污染困住，污染物将它的纯净形态腐化得面目全非。它现在愤怒而痛苦地游荡在诺莫瑞根的殿堂间，成为了瑟玛普拉格背叛留下的邪毒液体化身。",
        },
        [484] = {
        ["Title"] = "安图苏尔",
        ["Description"] = "安图苏尔希望自己的苏利萨斯蜥蜴们长得又壮又凶残。他对待这些周身长满鳞片的野兽如同家人一般，任何人——哪怕是巨魔，如果胆敢伤害他的宝贝孩子们，都会遭到他的无情痛击。安图苏尔不会给对手留下全尸，而是允许蜥蜴们对那些自己送上门来的倒霉蛋大快朵颐。",
        },
        [584] = {
        ["Title"] = "传令官沃拉兹",
        ["Description"] = "在天灾军团入侵艾卓-尼鲁布期间，这个王国内的许多住民都逃进了地下隧道以躲避巫妖王的爪牙。但有一些人却挖得太深，使尤格-萨隆的卷须暴露在外。尤格-萨隆被这一骚乱吵醒了，于是派出了一名最为古老的战士——传令官沃拉兹，前去屠杀陷入围困的蛛魔，并将任何威胁到上古之神的蠢货全部歼灭。",
        },
        [833] = {
        ["Title"] = "指挥官库鲁尔格",
        ["Description"] = "指挥官库鲁尔格四处搜寻危险的宝物来充实部落的军械库。他在停滞大厅中遭遇的不幸令他愤怒不已，当他发现有联盟冒险者想要偷走他的战利品时，就会变得越发怒不可遏。",
        },
        [831] = {
        ["Title"] = "莱登",
        ["Description"] = "当雷神误打误撞闯入了位于现今魔古山宝库下方的大厅时，他的人生彻底发生了改变。传说他发现了远古泰坦守护者看守着一个称为纳拉克煞引擎的不可思议装置。没人知道这两者之间到底发生了什么，但雷神离开该地的时候，已经拥有了建立帝国的力量。而守护者的命运，以及他如今的下落，仍然是一个谜。",
        },
        [829] = {
        ["Title"] = "神女双天",
        ["Description"] = "神女双天是雷神最为骁勇的部下之一。雷神为了维护自己的统治，让自己最信任的勇士守护在他身边，希望用她们卓绝的武力击溃来犯之敌。",
        },
        [585] = {
        ["Title"] = "看门者克里克希尔",
        ["Description"] = "在巫妖王抵达前，艾卓-尼鲁布的大门牢不可破地矗立了好几个世纪。这位名叫克里克希尔的蛛魔被变成了亡灵，被迫守护着他在生前没能成功守住的这道大门，仿佛是在嘲笑他连自己的家园都保护不了。",
        },
        [713] = {
        ["Title"] = "加拉隆",
        ["Description"] = "也许是时候重新考虑踩死虫子的问题了。别嘲笑这只巨兽的小翅膀，那会令他大发雷霆。",
        },
        [421] = {
        ["Title"] = "电刑器6000型",
        ["Description"] = "机械师瑟玛普拉格不是个有耐心的侏儒。他不喜欢通过一次次的实验去验证发明的安全性和有效性，而是急于将其投入使用，并将注意力转移到下一个项目上。电刑器6000型是他首次尝试通过超能机械坦克将电能作为武器的产物。“霹雳裤”汤姆·克兰科中尉是唯一一位驾驶过电刑器的幸存士兵，现在成了麻风侏儒里的大英雄。",
        },
        [485] = {
        ["Title"] = "殉教者塞卡",
        ["Description"] = "许久以前，殉教者塞卡在其拉与巨魔间爆发的激烈战争中不幸殒命。由于他的英勇牺牲，祖尔法拉克仍然屹立不倒。即便是在死后，他仍警惕地守护着自己挚爱的城市，据说，那些害死他的虫人战士们都受到了诅咒，化身为无脑圣甲虫，在殉道者身边奔来跑去。",
        },
        [586] = {
        ["Title"] = "哈多诺克斯",
        ["Description"] = "在艾卓-尼鲁布即将陷落之际，蛛魔大臣们放出了一只蜘蛛，这只蜘蛛能在一天之内就成长到足以挑战巫妖王的不死军团。哈多诺克斯心中充满了为死去族人复仇的渴望，要从任何胆敢威胁其家园的人手中夺回失地。",
        },
        [179] = {
        ["Title"] = "疯狂之缘——雷纳塔基",
        ["Description"] = "疯狂之缘里有四名充满仇恨的灵魂：可怕的巨魔格里雷克被神剥夺了再生能力；战士雷纳塔基以热衷施虐而臭名昭著；先知哈扎拉尔整日被恐怖的未来预言所折磨；强大的战将乌苏雷则拥有号令风暴的力量。",
        },
        [827] = {
        ["Title"] = "击碎者金罗克",
        ["Description"] = "雷电之王觉醒时，他将难以想象的力量赐予了那些最忠诚且最具野心的巨魔。狂战士金罗克是第一批接受雷神祝福的战士，虽然雷神的礼物几乎撕碎了他的身体，但这只巨魔现在已经拥有了在战斗中呼风唤雨的能力。",
        },
        [825] = {
        ["Title"] = "托多斯",
        ["Description"] = "千年以来，不断有零星的魔古血肉塑造魔法渗入雷电之王要塞下方的洞穴之中。黑暗能量使原本生活在内室中的一只龙龟产生了变异，使其融进了周围的水晶墙体。这个由血肉与顽石化成的生物名叫托多斯，从那以后，它便以洞穴中丰富的矿藏为食，体型变得无比巨大。",
        },
        [587] = {
        ["Title"] = "阿努巴拉克",
        ["Description"] = "艾卓-尼鲁布和阿努巴拉克的命运从很久以前就交织在一起了。在世时，他是帝国的国王；往生后，他成了帝国的征服者。这位叛徒国王奉巫妖王之命再次保护他的古老帝国，警惕着任何威胁他王国的人。",
        },
        [821] = {
        ["Title"] = "墨格瑞拉",
        ["Description"] = "即使是最为无情麻木的魔古族人，想到那些雷电之王指使的黑暗实验的时候都会不寒而栗。很久之前，一条年幼的云端翔龙被扭曲成了多头蛇，并被遗弃在了雷神的王座下。而今这个扭曲的生物潜伏在被遗忘的深渊中，等待着释放它那无尽的愤恨。",
        },
        [422] = {
        ["Title"] = "机械师瑟玛普拉格",
        ["Description"] = "杀人犯、叛徒、自我加冕的侏儒国王，机械师希科·瑟玛普拉格用破绽百出的铁腕手段统治着诺莫瑞根的放射性殿堂。他的臣民数量众多，邪恶疯狂，足以保护自己的疯王免受任何来自地上世界的侵袭。正是瑟玛普拉格的疯狂使他变得极其危险——他有着摧毁身边万物的狡诈，且丝毫不顾自身安危。难怪就连诺莫瑞根里的怪物都对他望而生畏。",
        },
        [486] = {
        ["Title"] = "巫医祖穆拉恩",
        ["Description"] = "强大的守护者邦桑迪照看着暗矛巨魔的灵魂，可许多沙怒部族亡故成员的灵魂却没能得到这样的庇佑。这些饱受折磨的灵魂服从于祖穆拉恩，这是一位残忍的巫医，用黑暗魔法迫使城中的亡者无止无休地为自己效命。",
        },
        [588] = {
        ["Title"] = "托尔戈",
        ["Description"] = "达卡莱是最为凶残的巨魔部族之一，而托尔戈又是最能够代表该部族野蛮程度的典范。但这个大块头怪物却发现与巫妖王相比，自己大肆吹嘘的暴行根本算不了什么，他现在尽心维护阿尔萨斯的统治，向任何胆敢挑战其权威的人发出可怕警告。",
        },
        [819] = {
        ["Title"] = "赫利东",
        ["Description"] = "赞达拉将强大的生物带到了雷神岛，把它们当做战争机器。在战神贾拉克的率领下，赞达拉驭兽者当中的恐龙统领用古老的部族法术强化这些巨兽，并使它们对自己唯命是从。赫利东是贾拉克的传说坐骑，它的锐角不费吹灰之力就能将要塞的石头城墙扯成碎片。",
        },
        [817] = {
        ["Title"] = "铁穹",
        ["Description"] = "有关铁穹和他魁麟勇士的传说流传甚广。这位无所畏惧的魔古族将领不顾代价、铁血无情地为帝国夺取了一场又一场胜利，雷电之王因此赐予他“铁穹”的称号。",
        },
        [749] = {
        ["Title"] = "指挥官玛洛尔",
        ["Description"] = "玛洛尔与其他几位勇敢的战士们甘愿冒一切风险守护血色十字军堡垒，使其免受亡灵的亵渎。但可悲的是，他们失败了。巴纳扎尔将这名无惧的指挥官腐化成了他的仆从，使这名狂热者不得不与自己生前最憎恨的仇敌同流合污。",
        },
        [589] = {
        ["Title"] = "召唤者诺沃斯",
        ["Description"] = "诺沃斯向来都是投机主义者，想方设法超越他的平庸同伴。这位邪教徒将自己的心从身体里挖了出来，在死前几秒将这个仍在跳动的器官献给了自己的主人。他的进取心令主人大受感染，于是将他重塑成了巫妖。",
        },
        [90] = {
        ["Title"] = "赫利克斯·破甲",
        ["Description"] = "赫利克斯从前是锈水财阀的工匠，可之后他收到了迪菲亚兄弟会的一笔款项，足以让他那样的无名部落工程师所做的一切都一文不值。就像所有精明的地精会做的那样，他迅速接下了这份工作，声称不再对前任雇主效忠。",
        },
        [423] = {
        ["Title"] = "诺克赛恩",
        ["Description"] = "诺克赛恩是腐化的活化身。瑟莱德丝命她的同伴维利塔恩造出这个腐烂的元素，充当强大的仆从和忠实的宠物，来化解公主心中的孤寂。诺克赛恩将自己的剧毒精华散播到玛拉顿尚未受到瑟莱德丝邪恶影响的其它区域。",
        },
        [487] = {
        ["Title"] = "耐克鲁姆和塞瑟斯",
        ["Description"] = "耐克鲁姆在寿命终结后，继续以不可思议的方式存活着，他日渐腐烂的身体仍能对塞瑟斯提供帮助。这两位巨魔共同为沉睡在城市中心的神秘巨兽加兹瑞拉搜罗祭品。许多巨魔追随者都迫不及待地等待着这只可怖怪物的苏醒，为了亲眼观看仪式不惜付出任何代价。",
        },
        [590] = {
        ["Title"] = "暴龙之王爵德",
        ["Description"] = "暴龙之王爵德是达卡莱巨魔中的传奇角色。相传，有个巨魔狩猎小队在天灾瘟疫肆虐的居住区里发现了一只强大的野兽。巨魔们驯服了这头魔暴龙，相信它自身就是一剂解毒药，能治愈在其族人中肆虐的可怕疫病。",
        },
        [180] = {
        ["Title"] = "疯狂之缘——乌苏雷",
        ["Description"] = "疯狂之缘里有四名充满仇恨的灵魂：可怕的巨魔格里雷克被神剥夺了再生能力；战士雷纳塔基以热衷施虐而臭名昭著；先知哈扎拉尔整日被恐怖的未来预言所折磨；强大的战将乌苏雷则拥有号令风暴的力量。",
        },
        [846] = {
        ["Title"] = "马尔考罗克",
        ["Description"] = "马尔考罗克在潘达利亚的整场战役中都是加尔鲁什最为忠诚和可靠的副官。当酋长需要有人自告奋勇接受亚煞极之力的灌注时，马尔考罗克顺理成章地站了出来，没有半点犹豫。",
        },
        [745] = {
        ["Title"] = "皇家宰相佐尔洛克",
        ["Description"] = "佐尔洛克尖声宣布旨意，以确保让庞大螳螂妖宫殿中的各个角落都能听得见。他代表女皇的意志，而且他的言辞本身也足以激起这些螳螂妖战士的斗志，让他们心甘情愿为女皇献出生命。佐尔洛克忠心不二，任何事情都动摇不了他守护挚爱女皇的信念，即便她身陷堕落，也矢志不渝。",
        },
        [591] = {
        ["Title"] = "先知萨隆亚",
        ["Description"] = "天灾军团势如破竹地挺近达克萨隆要塞，就算是盗取神灵之力也无力回天。眼看巨魔军团成群结队地在巫妖王身前倒下，萨隆亚现在只为自己的新主人预言胜利的捷报。",
        },
        [569] = {
        ["Title"] = "酋长卡加斯·刃拳",
        ["Description"] = "身为碎手氏族的首领和邪部落的酋长，卡加斯是近代最伟大的兽人英雄之一。多年来，他已经割断了双手，用战争武器取而代之，这是他氏族中特有的血腥仪式。他通过对别人和自己施加痛苦而获得快感，成为了兽人中的传奇人物。作为邪部落的残暴统治者，他用自己的残暴和凶猛打造出了一支势不可挡的作战大军，就驻扎在地狱火堡垒的钢铁城墙之内。",
        },
        [424] = {
        ["Title"] = "锐刺鞭笞者",
        ["Description"] = "创造锐刺鞭笞者的初衷是为了让它在玛拉顿里陪伴诺克赛恩。随着时间的推移，这一全新造物却成了公主最珍爱的仆从之一。锐刺鞭笞者与诺克赛恩并肩在洞穴里游荡，用长满尖刺的卷须将所能找到的任何未受污染的植物撕成碎片。",
        },
        [743] = {
        ["Title"] = "大女皇夏柯希尔",
        ["Description"] = "古老的卡拉克西组织刚刚作出了一个令人遗憾的决定——必须废黜堕落的大女皇夏柯希尔。依照传统，当螳螂妖统治者年老体衰之时，会由慎重选出的继任者继承王位。但由于夏柯希尔正当壮年，远未到退位的年纪，卡拉克西别无选择，惟有处死当今女皇。如果再不尽快对她采取行动，整个潘达利亚都将在螳螂妖贪婪虫群的席卷下粉碎殆尽。",
        },
        [592] = {
        ["Title"] = "斯拉德兰",
        ["Description"] = "斯拉德兰毕生都在为神灵效忠，牺牲神灵让他感觉不到半点儿愉悦。当神灵的力量涌入他的身体时，这位高阶先知发誓，一定要让天灾军团也同样品尝到达卡莱巨魔被迫忍受的暴行。",
        },
        [742] = {
        ["Title"] = "烛龙",
        ["Description"] = "传说有一条盘旋的金色翔龙，有时会出现在高空的云层之上，沐浴在阳光下。据个别声称亲眼看见过它的人说，这条翔龙会疯狂地保护平台。",
        },
        [741] = {
        ["Title"] = "风领主梅尔加拉克",
        ["Description"] = "风领主梅尔加拉克统领着螳螂妖不计其数的空中大军。尽管傲慢，这只狡诈的虫子却疯狂地保护着他的士兵，乐于与他们并肩作战。梅尔加拉克相信，螳螂妖是凌驾于所有其它种族之上的优等生物，坚信他的族群绝不会落败。",
        },
        [738] = {
        ["Title"] = "指挥官沃加克",
        ["Description"] = "指挥官沃加克以凶猛的战斗力弥补了自己战略部署方面的不足。他的毁灭性打击在螳螂妖顺利渡桥的过程中发挥了至关重要的作用，使其得以渗入岛屿。筹谋作战计划是螳螂妖将军的事，在沃加克心中只有对鲜血的渴望。",
        },
        [593] = {
        ["Title"] = "达卡莱巨像",
        ["Description"] = "巨魔们在施放巫毒法术时使用魔精的传统由来已久，但将那些法术融合在一起并注入魔像，却是极具革命性的突破。他们所取得的初步成功催生出了原始造物——巨像，并继而生成了无数哨兵。",
        },
        [737] = {
        ["Title"] = "琥珀塑形者昂舒克",
        ["Description"] = "急于取悦女皇的昂舒克创造出了几种新配方。可这位才华横溢的年轻炼金师却因与螳螂妖女皇过于亲近而被煞的负面能量攫住了心智。现在，对失败的过度恐惧迫使昂舒克不断研制越发致命的武器。",
        },
        [425] = {
        ["Title"] = "工匠吉兹洛克",
        ["Description"] = "工匠吉兹洛克与其它五位宝石猎人溜进了玛拉顿，前去采集异常珍贵的水晶。但当其它队友都被萨特屠戮殆尽，仅有吉兹洛克一人幸存时，他才明白他们的计划全盘落空了。这位地精被困在洞穴里，濒临疯狂的边缘，将自己视作这个地下王国的新统治者。",
        },
        [489] = {
        ["Title"] = "乌克兹·沙顶",
        ["Description"] = "乌克兹·沙顶沉迷于为自己的部族寻获往日的荣光。在他的成长过程中，早已对那些辉煌时代的传奇故事耳熟能详。那时，巨魔们在其古老的土地上称王称霸，完全没有地精、侏儒、海盗、食人魔以及其它外来入侵者的滋扰。沙顶邀请他的挚友卢兹鲁帮他一同书写沙顶历史的新篇章，用自己部族仅有的方式——追风掣电且目空一切的暴力，重振雄风。",
        },
        [594] = {
        ["Title"] = "莫拉比",
        ["Description"] = "达卡莱巨魔想要牺牲犸托斯来获得力量，结果这位神灵却选择了自我毁灭。尽管如此，人们却不能否定莫拉比身上堪比神灵的力量。这位高阶先知饮下了神灵的鲜血，现在会不惜一切代价确保祖达克牢牢掌握在巨魔手中。",
        },
        [181] = {
        ["Title"] = "高阶祭司基尔娜拉",
        ["Description"] = "高阶祭司娅尔罗被杀之后，豹神贝瑟克选择她的妹妹基尔娜拉作为其新的凡间代表。为了支持最近古拉巴什和赞达拉部落达成的协议，基尔娜拉放出了她凶残的猎豹，来对付这里不设防的住民。",
        },
        [850] = {
        ["Title"] = "纳兹戈林将军",
        ["Description"] = "纳兹戈林将军曾经是前任酋长萨尔麾下的一名步兵，在灰熊丘陵和沉没之城瓦丝琪尔取得大捷后，他便迅速得到了晋升。纳兹戈林对部落无比忠诚，且深受严格的荣誉与责任法典的束缚，他义无反顾地为酋长戍守着阵线，至死方休。",
        },
        [677] = {
        ["Title"] = "皇帝的意志",
        ["Description"] = "宝库的中央内室里有一群静静矗立了数个世纪的石头战士。这些战士雕像列队整齐，时刻准备迎接一场从未打响的战斗。它们守护着怎样的财宝？它们与埋藏在这暗影中的泰坦之力又有什么关联？",
        },
        [595] = {
        ["Title"] = "凶残的伊克",
        ["Description"] = "在对其进行多次武装未果后，达卡莱巨魔将他们失败的鳄鱼人实验封印了起来。当冰巨魔奋力抵抗入侵其挚爱家园的天灾军团时，伊克和他的仆从们则在等待着重获自由的时刻。",
        },
        [682] = {
        ["Title"] = "缚灵者戈拉亚",
        ["Description"] = "赞达拉巨魔的领袖对宝库发起了攻击，戈拉亚决意要掘开这蕴满古老奥秘的宝藏，将他所能找到的一切奥术能量和知识带回来。戈拉亚拥有过人的黑暗天赋，身边又有一群对他忠心耿耿的赞达拉密士，他已冲破外墙，在彻底夺取宝库之前，决不会停手。",
        },
        [426] = {
        ["Title"] = "格里哈斯特",
        ["Description"] = "上古之神发出召唤，格里哈斯特便积极响应，一路杀进黑暗深渊，用双剑消灭了十余个措不及防的暮光之锤邪教徒。邪教徒们对他的凶猛与残暴感到震惊不已，于是就同意让他留下，而他也建造了自己的祭坛，献上贡品，并接受来自于神庙废墟邪恶气息中的祝福。",
        },
        [690] = {
        ["Title"] = "杰翰",
        ["Description"] = "当魔古部族正忙着争权夺利时，狡诈的杰翰带领他那令人闻风丧胆的蜥蜴人奇袭小队，进入了宫殿传说中的藏宝室。这些凶残的爬虫类生物很少会把“忠诚”二字当回事，可杰翰的胆魄和雄才伟略还是在他的部下心中激起了某种荣誉感。",
        },
        [596] = {
        ["Title"] = "迦尔达拉",
        ["Description"] = "当天灾军团侵入祖达克时，达卡莱巨魔只得求助自己的神灵。冰巨魔并没有选择对神灵俯首乞怜，而是拿起了利刃。眼看遇害的神灵阿卡里血流成河，高阶先知迦尔达拉和他那些背弃信仰的追随者们将神灵的力量据为己有。",
        },
        [725] = {
        ["Title"] = "萨莱斯的兵团",
        ["Description"] = "残忍的蜥蜴人游荡在潘达利亚大陆上，盗取能够找到的一切，使其他住民不得安宁。在这些爬虫掠夺者当中，最为臭名昭著的便是头领萨莱斯和他麾下的兵团。多年以来，这位蜥蜴人指挥官在炮舰上率领骑兵们在四风谷中大肆横行，炮舰是一头体型庞大、装备精良的穆山兽，与其说是坐骑，它更像是一座移动堡垒。",
        },
        [852] = {
        ["Title"] = "伊墨苏斯",
        ["Description"] = "潘达利亚的古老住民意识到了能量池的重要作用，它具有赋予生命的力量，于是他们修建了地下水利系统来守护水源，并滋养着锦绣谷中的万众生灵。腐化之触驱动并扭曲了这些水源，伊墨苏斯则成为了山谷悲伤的邪恶化身。",
        },
        [698] = {
        ["Title"] = "武器大师席恩",
        ["Description"] = "在艾泽拉斯，武器大师的头衔有许多种定义。对于席恩来说，这意味着魔古族所有的兵器都对他唯命是从。这位国王不吝于向任何胆敢质疑他对潘达利亚统治权的人展示他那勾魂夺命的技能。",
        },
        [597] = {
        ["Title"] = "比亚格里将军",
        ["Description"] = "洛肯的钢铁大军浩浩荡荡，而比亚格里将军则是他们当中最为骁勇善战的大将。作为洛肯闪电大厅的先锋尖兵，这位铁血指挥官要确保对任何擅闯主人领地的不速之客施以极端惩罚。",
        },
        [91] = {
        ["Title"] = "死神5000",
        ["Description"] = "迪菲亚的工程师们在死神4000的基础上，日以继夜地想要完善出一种新型号的收割机。兄弟会相信，只要完工，这台恐怖的机械就能像割麦子一样从暴风城的武装士兵中杀出一条路来。",
        },
        [427] = {
        ["Title"] = "维利塔恩",
        ["Description"] = "萨特领主维利塔恩在闻听玛拉顿遭到腐化的消息后，便动身前往玛拉顿深处。瑟莱德丝起初对这位少言寡语的恶魔颇有戒心，但维利塔恩用行动证明了自己的价值，他为公主造出了许多邪恶的仆从，如诺克赛恩和锐刺鞭笞者。",
        },
        [694] = {
        ["Title"] = "阿达罗格",
        ["Description"] = "怒焰裂谷穴居人的人数近几个月急剧下跌，原因只有一个：阿达罗格。这只贪吃的怪物是最早被黑暗萨满从火焰之地赶离的烈焰猎犬之一。阿达罗格的驯养员每天喂这只狂怒难驯的巨兽吃超出他体重两倍的穴居人肉块，以使他心平气和。",
        },
        [598] = {
        ["Title"] = "沃尔坎",
        ["Description"] = "洛肯的强大军团是他的铁匠沃尔坎精湛技艺的绝佳证明。他的工作永无止息，不眠不休地塑造士兵为堕落的守护者作战。只有当铁匠的创造物踩在敌人破碎的尸体上时，他的工作才算告一段落。",
        },
        [726] = {
        ["Title"] = "伊拉贡",
        ["Description"] = "致力于发展帝国的魔古族，大肆挽救泰坦科技并使其为己所用，在这古老的复合体内生成了难以言喻的力量。至于为什么要把这些能量汇聚在一条神龙的冰冷意志之中，则是深埋在宝库深处的神秘谜团。",
        },
        [686] = {
        ["Title"] = "祝踏岚",
        ["Description"] = "恨之煞腐化了影踪派强大而务实的首领——祝踏岚。曾致力于保护自己挚爱家园的祝踏岚，如今对世间万物憎恨不已，恨之煞扭曲了他对身边所有事物——以及所有人的看法。",
        },
        [683] = {
        ["Title"] = "无尽守护者",
        ["Description"] = "当惧之煞入侵永春台时，三位德高望重的锦鱼人守护者遭到了腐化，他们背叛了其他的凡人看守，将他们悉数杀死。这几位扭曲的守护者现在已将注意力转移，意图消灭任何胆敢妨碍煞魔粉碎当地最后一道防线的好事者。",
        },
        [599] = {
        ["Title"] = "艾欧纳尔",
        ["Description"] = "洛肯从不依赖他的钢铁仆从来充当他的个人卫兵。这位堕落的守护者靠的是闪电本身的力量。艾欧纳尔和它的同伴得以在闪电大厅中避难栖身，作为交换，它们必须献出绝对的忠诚和意愿，消灭任何妄图对抗自己新主人的闯入者。",
        },
        [727] = {
        ["Title"] = "翼虫首领尼诺洛克",
        ["Description"] = "翼虫首领尼诺洛克凭借他那迅猛有力的攻击赢得了上百场战斗。在尼诺洛克看来，如果换成他领军，那么将军帕瓦拉克的小小成就跟他的大捷比起来简直不值一提。虽然他的空中攻势已经消灭了寺院的守卫，但这只长翅膀的怪物还在寻找机会，以证明自己的能力远在将军之上。",
        },
        [428] = {
        ["Title"] = "被诅咒的塞雷布拉斯",
        ["Description"] = "在闻听叔叔的悲惨命运后，高尚的塞雷布拉斯立誓解救扎尔塔的灵魂。可当这位林地守护者在玛拉顿的阴暗廊道间遭逢恐惧时，他全然没有做好准备。塞雷布拉斯很快就被四处弥漫的黑暗能量席卷，心中充满了不可控制的愤怒。",
        },
        [679] = {
        ["Title"] = "石头守卫",
        ["Description"] = "宝库的第一道防线是由一排被施了魔法的凶残雕像组成的。这些充当哨兵的石头守卫坚硬如花岗岩，不眠不休地警戒着，形成了由烈焰、毒牙和利爪筑起的难以逾越的围墙。",
        },
        [600] = {
        ["Title"] = "洛肯",
        ["Description"] = "他是艾泽拉斯头号守护者，负责看管尤格-萨隆，确保将它安妥地关押在囚牢里。没人能预见到，从上古之神的手中救下这个世界的守护者，却是想要毁灭世界的罪魁祸首。",
        },
        [728] = {
        ["Title"] = "血卫士伯鲁恩",
        ["Description"] = "在血腥围攻沙塔斯期间，伯鲁恩对一块德莱尼圣骑士领地发起了猛攻，这些圣骑士当时将自己封锁在城市的角落里。他单枪匹马地消灭了防御者，但却身负重伤，无法跟随部落加入此后突袭艾泽拉斯的行动。现在，伯鲁恩的伤势已经痊愈，他在地狱火堡垒中为卡加斯酋长效力，希望能弥补自己错过那些辉煌战役的遗憾，并用敌人的鲜血在兽人历史上写下自己的名字。",
        },
        [856] = {
        ["Title"] = "库卡隆黑暗萨满",
        ["Description"] = "哈洛姆和卡德里斯训练了数千名萨满祭司，他们会虔诚地对元素发出低声呼唤，令其助自己一臂之力。然而，加尔鲁什的大军从来不会“请求”——他们只会以正统部落的名义夺走想要的东西。黑暗萨满们强迫元素对其唯命是从，把它们扭曲成了燃灭灰烬、腐化之水和剧毒空气。",
        },
        [670] = {
        ["Title"] = "破桶而出的炎诛",
        ["Description"] = "为拯救家族酿酒厂，高老伯决定破釜沉舟，研制一种他心目中的终极麦酒——一种口感醇厚的浓烈饮品。他这大胆的实验却催生了一个可怕又极为暴躁的酒灵生物，被称作破桶而出的炎诛。",
        },
        [601] = {
        ["Title"] = "法瑞克",
        ["Description"] = "这位天灾军团的勇士是阿尔萨斯夺取霜之哀伤后创造的首批死亡骑士之一。明知那会是一场注定失败的战役，但法瑞克还是忠诚地追随自己的王子去了诺森德，结果他穷尽毕生精力为其效命的王子却背叛了他。",
        },
        [729] = {
        ["Title"] = "雷施",
        ["Description"] = "在无数岁月里，这位友好的水灵都带着孩童般的天真与欢欣，在永春泉里嬉戏玩耍。当这片平静之地被战乱席卷时，雷施看见原本澄净如晶的水面突然变得混浊晦暗，于是她陷入了恐惧。",
        },
        [429] = {
        ["Title"] = "兰斯利德",
        ["Description"] = "多年以来，名叫兰斯利德的古老山巨人都在玛拉顿料理着璀璨的水晶花园，但自打瑟莱德丝来此定居的那天起，兰斯利德便深深地被她吸引住了。这位曾经清心寡欲的巨人现今生活的唯一目的就是为他残暴的新主人效力。",
        },
        [666] = {
        ["Title"] = "莉莉安·沃斯",
        ["Description"] = "课程：再教育\r\n\r\n亡灵莉莉安·沃斯因背叛而亲手扼死了她的父亲——血色十字军的高阶祭司，然后大举出击，最终进入了通灵学院的阴暗走廊。可黑暗院长加丁不但没有下令消灭这位入侵者，反而仁慈地指引沃斯，教她如何拥抱无情的命运。她那曾经指向学院的怒意和利刃，现在对准了加丁的敌人。",
        },
        [602] = {
        ["Title"] = "玛维恩",
        ["Description"] = "玛维恩自小就认识阿尔萨斯，他不仅将他视作王子，还把他当成自己真正的朋友。当阿尔萨斯向着诺森德起航时，这位骑士毫不犹豫地跟他一起上路。尽管他的朋友后来将一把被诅咒的符文之刃插进了他的心脏，但眼见王子变成了这副模样，玛维恩还是体会到了心碎的感觉。",
        },
        [665] = {
        ["Title"] = "血骨傀儡",
        ["Description"] = "课程：复生导论第一章\r\n\r\n人们都以为血骨傀儡在安多哈尔被消灭了，但加丁很快就将这个狂躁的畸形巨怪复生了，并让他来阻挡入侵者。黑暗院长还设法将血骨傀儡变得更为凶残，使其贪得无厌地尽享敌人的血肉。",
        },
        [858] = {
        ["Title"] = "青龙玉珑",
        ["Description"] = "玉珑发起的是智慧和信念的试炼。关于晦暗未来的预感在皇帝少昊的脑海中久久徘徊，于是他爬上了不息山，想要听取青龙的意见。玉珑力劝少昊抛开困扰他的负面情绪，在完成试炼后，皇帝少昊与这片土地融为了一体。要想通过青龙的试炼，战士们必须睿智地作出选择。",
        },
        [649] = {
        ["Title"] = "莱公",
        ["Description"] = "这个体型庞大的螳螂妖是攻击城门的开路先锋。莱公扮演着活体攻城槌的角色，无情地冲击着熊猫人的防线，妄图消灭阻挡螳螂妖凶残入侵潘达利亚的任何障碍。",
        },
        [603] = {
        ["Title"] = "逃离阿尔萨斯",
        ["Description"] = "两位领袖想要在一场对决中击败巫妖王，但她们很快便发现，自己根本不是这位真正王者的对手。为了活命，她们试图逃离他的圣所。她们每一刻徒劳的挣扎都令阿尔萨斯感到乐在其中。有些事情是无论如何都躲不掉的。",
        },
        [643] = {
        ["Title"] = "残忍的斯卡迪",
        ["Description"] = "维库人往往根据个人所取得的成就来赋予绰号。肃清一条达卡莱血脉或是消灭一只牦牛人，可以获得“忠实”的称谓，但必须要足够邪恶才能被称作“残忍”。很久以前，有维库人出手保护被伊米隆定罪的畸变婴儿即人类始祖，斯卡迪残酷无情地对其展开了追杀，因此获得了“残忍”的称号。",
        },
        [430] = {
        ["Title"] = "洛特格里普",
        ["Description"] = "有传闻说，洛特格里普已经在玛拉顿的地下水域中居住了上千年。大名鼎鼎的矮人猎手赫米特·奈辛瓦里有一次宣称无人能找到这野兽，于是就有许多业余追踪者接受挑战，立志寻找这只传说中的生物。结果全都有去无回。",
        },
        [635] = {
        ["Title"] = "纯洁者耶德瑞克",
        ["Description"] = "在英雄们得以挑战黑暗大军之前，他们必须击败圣光的代言人。大领主提里奥·弗丁从银色北伐军中挑选出了一位令人敬畏的圣骑士来测试挑战者的勇气。耶德瑞克会不遗余力地剔除那些缺乏道德信念的人，将他们隔绝在冰冠堡垒之外。",
        },
        [604] = {
        ["Title"] = "克莱斯塔卢斯",
        ["Description"] = "单凭力量无法赢得战争，要靠军队才行。这一固有真理激励着洛肯，他命人创造忠实的岩石生物来壮大自己的军团。克莱斯塔卢斯奉命监管这一作业，并粉碎任何威胁其运作的闯入者。",
        },
        [630] = {
        ["Title"] = "拉文索尔",
        ["Description"] = "冷酷狡诈的塞安妮苟萨释放出了熔火恶犬。达拉然的法师们将这只双头怪物囚禁在监狱里，原本是希望能平复它对鲜血的渴望，但多年的隔离生活丝毫没能改变它的本性。",
        },
        [860] = {
        ["Title"] = "白虎雪怒",
        ["Description"] = "雪怒同时发起了力量与抑制力的试炼。当雪怒给了皇帝少昊一根十尺长杆，命他攻击雪怒的一位战士时，皇帝根本无法击中目标。若是没有抑制力，愤怒和仇恨就会变得失去控制。要想通过白虎的试炼，战士们必须学会驾驭他们的力量，并压下自己暴力的念头。",
        },
        [112] = {
        ["Title"] = "欧泽鲁克",
        ["Description"] = "从深岩之洲的创造之初，欧泽鲁克就一直是巨石之核的守护者。没有人知道他为什么还留在那里，但是很明显，这名守护者认为任何对死亡之翼不利的人，都是深岩之洲的威胁，应该予以消灭。",
        },
        [605] = {
        ["Title"] = "悲伤圣女",
        ["Description"] = "洛肯统御着奥杜尔的军队，但这些士兵并非全都心甘情愿地为他效命。悲伤圣女奉命消灭任何妄图在岩石大厅起事的叛逆者。与昔日盟友为敌令她感到无比悲伤，但她履行使命的手段却异常残暴。她甚至将悲伤都化作了武器。",
        },
        [92] = {
        ["Title"] = "撕心狼将军",
        ["Description"] = "詹姆斯·哈林顿身中狼人的诅咒后，在一个刺骨的寒夜里毁掉了钟爱的家庭和他的远洋航海生涯。在那之后，他改名为撕心狼，逃到了西部荒野，之后更是接受了任命，指挥迪菲亚兄弟会可怖的无畏舰。",
        },
        [431] = {
        ["Title"] = "瑟莱德丝公主",
        ["Description"] = "土元素瑟莱德丝——石母塞拉赞恩的独生女，住在玛拉顿的内室深处。由于受到上古之神的影响，这位公主将她的家园变成了充满腐化与痛苦的窝巢。有人认为，她的邪恶力量是与她已故爱人扎尔塔的灵魂紧密联系在一起的。假如果真如此，那么解放这位陨殁的林地守护者，也许是净化玛拉顿的唯一希望所在。",
        },
        [124] = {
        ["Title"] = "神殿守护者安努尔",
        ["Description"] = "泰坦在控制起源大厅最后的日子里，将掌握圣光所需的意志力赐予了神殿守护者安努尔。他被授命裁判那些进入神殿的人们是否纯洁。",
        },
        [606] = {
        ["Title"] = "远古法庭",
        ["Description"] = "埃比德努、卡德拉克和玛尔纳克组成了远古法庭，它们是蕴藏着艾泽拉斯古老历史的泰坦圆盘的管理者。这些无法移动的构造体决不会轻易放弃泰坦的秘密，但像布莱恩·铜须这样勇敢的冒险者却坚信，只有高风险才能换来高回报。",
        },
        [184] = {
        ["Title"] = "赞吉尔",
        ["Description"] = "因为把同胞变成了丧失心智的奴隶，赞吉尔一度被劈颅部落所放逐。为了壮大祖尔格拉布的军队，碎神者金度让他在古拉巴什中任职。众所周知，起死回生这种事，对他的药剂来说一点都不难。",
        },
        [104] = {
        ["Title"] = "厄祖玛特",
        ["Description"] = "传说艾泽拉斯的大海出现过巨大的海怪，然而这个神话如今成了真。纳迦通过某种邪恶的手段，将海怪的宗主厄祖玛特控制在手下，用来对付耐普图隆及其追随者们。",
        },
        [626] = {
        ["Title"] = "埃雷克姆",
        ["Description"] = "紫罗兰监狱中的住民都是外域最为臭名昭彰的罪犯，埃雷克姆也不例外。虽然他刺杀六人议会的阴谋被肯瑞托识破了，但现在蓝龙军团却征召了这位阴险的鸦人去实现更有野心的目标——荡除达拉然的所有法师。",
        },
        [607] = {
        ["Title"] = "塑铁者斯约尼尔",
        ["Description"] = "尽管有些人在抵抗洛肯的腐化，但塑铁者斯约尼尔却迫不及待地要将意志熔炉用于更加黑暗的用途。他认为自己全新的创造物要大大优于土灵，并用它们对付那些胆敢违抗洛肯意志的敌人。",
        },
        [368] = {
        ["Title"] = "加摩拉",
        ["Description"] = "当暮光之锤邪教到来时，其信徒唤醒并囚禁了艾露恩神殿中的最后一只宁静生物。邪教徒们乐此不疲地将这只巨龟折磨了许多年，将它的灵魂与身体剥离，直到它终于陷入疯狂。他们把这只巨龟命名为加摩拉，训练他攻击外来者，守卫上古之神的圣地。",
        },
        [432] = {
        ["Title"] = "图特卡什",
        ["Description"] = "尽管图特卡什在变成不死族前，已在蜘蛛大战中双目失明，他却仍是最致命的地穴恶魔指挥官，并以此身份继续为天灾军团效命。图特卡什用精美且近乎隐形的蛛网装点自己的巢穴，使其能感觉到每一次脚步声和最轻微的碰触，在收到警报后立即派出爪牙大军伏击那些倒霉的猎物。剧烈的震动会让他陷入莫名的狂怒，把他从剃刀高地内的隐蔽藏身处吸引出来。",
        },
        [115] = {
        ["Title"] = "阿尔泰鲁斯",
        ["Description"] = "巨大的风暴之龙阿尔泰鲁斯不知疲倦地警戒着旋云之巅的浮空平台。风暴的能量在他体内脉动，传说他还驾驭着驱动着这座元素堡垒的气流。",
        },
        [608] = {
        ["Title"] = "熔炉之主加弗斯特",
        ["Description"] = "加弗斯特制造出了一些极具杀伤力的致命武器，天灾军团用它们来对付活人。他能以极快的速度将萨隆邪铁铸成带来痛苦与死亡的可怕器具，也能直接将萨隆邪铁掷向自己的敌人。",
        },
        [99] = {
        ["Title"] = "沃登勋爵",
        ["Description"] = "高弗雷的左右手、傲慢的沃登勋爵曾发誓不惜一切代价，也要为其同胞鞠躬尽瘁。如今成了亡灵的一员，沃登依然信守诺言，用冰与毒对敌人发起猛攻。",
        },
        [864] = {
        ["Title"] = "钢铁战蝎",
        ["Description"] = "这只机械怪物是加尔鲁什攻城武器中的擎天柱石，威慑与毁灭是它几乎同等重要的两大用途。钢铁战蝎的原型是强大的库卡隆战蝎，它戍守着奥格瑞玛的大门，将任何胆敢挑战加尔鲁什正统部落权威的人撕成碎片。",
        },
        [622] = {
        ["Title"] = "审讯者达库斯",
        ["Description"] = "那些违犯玛里苟斯律法的人，都会发现自己置身于达库斯的利爪下。在把克莉斯塔萨关进魔枢后，达库斯便返回魔环接管新的囚犯——那些违抗玛里苟斯命令的其它龙族。他不在乎所谓的名誉，也无需虚伪的遮掩。他的囚犯就是要在这里受刑的，谁也别想逃脱。",
        },
        [609] = {
        ["Title"] = "伊克和科瑞克",
        ["Description"] = "身材矮小的亡灵法师科瑞克监管着萨隆矿坑中的工人。他待在可怕的伊克身上，能安全地朝敌人投掷毒药、炸弹或施放黑暗法术。他的工作还使他能够了解到冰冠堡垒中有助于推翻巫妖王统治的黑暗秘密。",
        },
        [369] = {
        ["Title"] = "审讯官格斯塔恩",
        ["Description"] = "暮光之锤邪教徒格斯塔恩向来沉湎于对生活品质的不懈追求：异国料理，靓衣华服，就连刑具都是由宝石镶嵌而成。囚徒那令人毛骨悚然的哭号声日夜在黑铁部族声名狼藉的禁闭室中回响。格斯塔恩骄傲地宣称，这骇人的声音是她谱写的尖叫交响曲，而她则时刻渴望在这和声中加入新的音色。",
        },
        [433] = {
        ["Title"] = "火眼莫德雷斯",
        ["Description"] = "莫德雷斯本是一位兽人萨满，奉命从奥格瑞玛前往剃刀高地调查那里的异常情况，结果却惨遭杀害，变成了为天灾军团效命的亡灵仆从。剃刀沼泽的领主觉得元素靠不住，于是便在莫德雷斯体内灌注了恶魔能量，使其灼烧活人的血肉。讽刺的是，莫德雷斯生前被唤作精神领袖，现在却用他的力量使那些低等亡灵陷入疯狂。",
        },
        [621] = {
        ["Title"] = "克莉斯塔萨",
        ["Description"] = "克莉斯塔萨所遭受的折磨究竟是因为莎拉苟萨之死，还是因为玛利苟斯想要将其转变成自己的配偶，答案只有玛里苟斯自己才知道。不论他意图如何，她的同伴一直都听得见她痛苦的哭喊和她神智渐失所发出的不安低语声。",
        },
        [610] = {
        ["Title"] = "天灾领主泰兰努斯",
        ["Description"] = "死亡骑士泰兰努斯在消灭巨人加弗斯特后，被提拔成了天灾领主。他凭借自身的力量、凶残和冰龙霜牙，成为了萨隆矿坑和映像大厅入口处最杰出的守护者。",
        },
        [185] = {
        ["Title"] = "碎神者金度",
        ["Description"] = "在祖尔格拉布被打败之后，金度的灵魂因为没能满足哈卡的愿望而饱受折磨。但最近，他通过某种办法回到了现世，并奴役了夺灵者。带着恐怖神灵的可怕力量，金度誓要恢复古拉巴什帝国往日的辉煌。",
        },
        [866] = {
        ["Title"] = "诺鲁什",
        ["Description"] = "有人说魔古族就是依照这个巨型魔像被创造出来的，他在潘达利亚地下深处看守并保卫着这片大陆上最为黑暗和危险的秘密。",
        },
        [110] = {
        ["Title"] = "克伯鲁斯",
        ["Description"] = "死亡之翼奴役着巨大的旋岩虫克伯鲁斯，让它挖掘大量源质来为黑龙之王铸造新的盔甲。但是，当死亡之翼离去后，愤怒的克伯鲁斯摆脱了管束，在巨石之核里盲目地肆虐。",
        },
        [611] = {
        ["Title"] = "肉钩",
        ["Description"] = "自从阿尔萨斯王子首次遭遇天灾憎恶以来，它们的邪恶程度与日俱增。肉钩比其它同类成长得更加强壮、更具毒性、更加敏捷。创造肉钩的亡灵法师们从没想过要把它塑造得多么聪明，但哪怕是最基本的狡诈就足以让它为所欲为了。",
        },
        [370] = {
        ["Title"] = "洛考尔",
        ["Description"] = "洛考尔对火元素诡计多端、尔虞我诈的本性感到深恶痛绝。为了能远离在熔火之心中效力的其它同类，这个强大的生物故意惹得火焰之王大发雷霆。洛考尔为自己的危险之举付出了代价——他被逐出了熔火之心，被迫孤绝地守卫黑石深渊。",
        },
        [434] = {
        ["Title"] = "暴食者",
        ["Description"] = "暴食者这三个字，对这庞大的憎恶来说绝对当之无愧，他大多数时间都在剃刀高地里吞食野猪及其野猪人主子的腐烂尸体。在他肠胃里堆积的恶臭肉块，使他周身弥漫着剧毒气体和致残疫病，让那些靠得太近的不幸冒险者窒息而死。",
        },
        [618] = {
        ["Title"] = "大魔导师泰蕾丝塔",
        ["Description"] = "相传大魔导师泰蕾丝塔是凯尔萨斯王子的导师。尽管她认为王子与纳迦结盟的做法愚不可及，她还是无法支持达拉然囚禁自己的人民。玛里苟斯只需要让她想起这一背叛，就会让她自愿给他的法师猎手充当导师。",
        },
        [612] = {
        ["Title"] = "塑血者沙尔拉姆",
        ["Description"] = "塑血者沙尔拉姆是在通灵学院下方殿堂中受训的首批亡灵法师之一。在他眼里，那些试验品以及偶尔出现的小学徒都只不过是他工作中的有趣点滴而已。现在所有住在斯坦索姆里的人，以及那些妄图阻止他的闯入者，都成了他的实验对象……",
        },
        [114] = {
        ["Title"] = "大宰相埃尔坦",
        ["Description"] = "奉主人阿萨德之命，埃尔坦守护着旋云之巅，对抗任何来自地面的、胆敢侵入这个领域的人们。为了履行其职责，这名元素贵族被授予了统御闪电和风暴大军的特权。",
        },
        [868] = {
        ["Title"] = "迦拉卡斯",
        ["Description"] = "督军扎伊拉和加尔鲁什在暮光高地的事件期间形成了非常亲密的关系，且她和她的龙喉兽人承诺为加尔鲁什的事业效忠。骇人的迦拉卡斯是所有龙族灾变祖先的直系后裔，扎伊拉骑在它的背上，监管着奥格瑞玛的海防力量。",
        },
        [581] = {
        ["Title"] = "塔达拉姆王子",
        ["Description"] = "在凯尔萨斯攻击巫妖王落败后，他麾下一些最为精锐的血精灵追随者惨遭杀戮，被复活成了名为黑暗堕落者的不死怪物。塔拉达姆王子就是这些狡诈嗜杀的吸血鬼生物中的一员。为了尽心竭力地取悦巫妖王，他进入了饱受战争摧残的安卡赫特深处寻找失落的蛛魔圣物，用以强化那些凶暴的亡灵蛛魔。",
        },
        [613] = {
        ["Title"] = "时光领主埃博克",
        ["Description"] = "永恒之龙军团通常会尝试使用精妙的手法来操纵时间流，但若是失败，他们也会派出刺客去直接“处理”阿尔萨斯。尽管人们对永恒之龙军团的刺客知之甚少，但这位埃博克却有着可怕的时间驾驭能力，使他成为了横亘在阿尔萨斯未来道路上的致命障碍。",
        },
        [371] = {
        ["Title"] = "驯犬者格雷布玛尔",
        ["Description"] = "格雷布玛尔·蚤须的猎犬是他唯一的家人。早在许久以前，这个矮人孤儿就开始繁育幼犬，把它们当做同伴和看门狗。要是驯犬者没有带上他的忠实跟班在黑石深渊里巡逻，那他通常就是在黑铁酒吧里喂它们喝上好的黑铁啤酒。",
        },
        [435] = {
        ["Title"] = "寒冰之王亚门纳尔",
        ["Description"] = "巫妖亚门纳尔及其麾下的军队在第三次战争期间参与了天灾军团对海加尔山的入侵。后来，巫妖王命他的仆从留在卡利姆多，继续扩大天灾军团的声势，于是亚门纳尔就计划建立一个据点，以便攻击奥格瑞玛和塞拉摩。结果，亚门纳尔把目光投向了那些刚刚失去家园、绝望无助的野猪人，承诺赋予他们超越死亡的力量，使其能够驱逐任何闯入其领土的入侵者。",
        },
        [125] = {
        ["Title"] = "地怒者塔赫",
        ["Description"] = "地怒者塔赫墓穴外的沙地上铺满了累累白骨，他们都是被安努尔所净化的生物。据说塔赫能操纵这些遗骨，让它们去对付那些成功绕开第一道防线的入侵者。",
        },
        [154] = {
        ["Title"] = "风之议会",
        ["Description"] = "随着南风之主希亚玛特被尼斐塞特所囚，奥拉基尔的副手们踊跃地想要填补风之议会里出现的权力真空。剩下的三名议会成员分别是：能够使用狂风治疗盟友或毒伤敌人的西风之主安舍尔，能够用风暴将最宏伟的山脉磨成齑粉的东风之主洛哈西，而葬送在北风之主尼希尔冰冷风暴之下的性命，比其他三位加起来的还要多。",
        },
        [186] = {
        ["Title"] = "埃基尔松",
        ["Description"] = "作为阿曼尼部落曾经的萨满，埃基尔松驾驭空气元素的本领令人赞赏。他的天赋被妖术领主玛拉卡斯所看中，这也让巨魔们的鹰神没费太大力气就找到了合适的宿主。",
        },
        [870] = {
        ["Title"] = "潘达利亚战利品",
        ["Description"] = "当加尔鲁什凝视潘达利亚时，他看到了未经发掘的力量。在他领军作战的过程中，加尔鲁什掠夺了熊猫人、魔古和螳螂妖的各种武器、财宝和遗物。他将它们存放在地下基地深处的仓库里，由起源自泰坦的神秘安全系统看守。",
        },
        [583] = {
        ["Title"] = "埃曼尼塔",
        ["Description"] = "长久以来，在安卡赫特潮湿而不见天日的洞穴中，繁密地生长着各类真菌和其它温和的生物形态。然而就在不久前，尤格-萨隆将它的压迫意志散播进了这个原本独立的生态系统。上古之神用它强大的腐化之力将安卡赫特一只土著野兽扭曲成了畸形的生物，它就是埃曼尼塔，这是个毫无思想的恐怖怪兽，周身充满了从其主人身上取得的破坏能量。",
        },
        [615] = {
        ["Title"] = "布隆亚姆",
        ["Description"] = "布隆亚姆从早期开始就一直在为诅咒神教效力了。克尔苏加德亲自向布隆亚姆传授了灵魂的价值，以及如何让灵魂饱受折磨，为天灾军团的战争机器供给能量。他能腐化敌人的灵魂，并将其一块一块地撕扯出来，这使他成为了看守巫妖王灵魂洪炉的理想守护者。",
        },
        [372] = {
        ["Title"] = "秩序竞技场",
        ["Description"] = "对黑铁矮人犯下罪行的人只会受到一种惩罚，那就是死亡。根据古老的传统，有罪之人要在秩序竞技场中对抗来自艾泽拉斯的各种凶残生物，付出鲜血的代价。迄今为止，没人知道是否有在这残忍酷刑中侥幸生还的幸运儿。",
        },
        [436] = {
        ["Title"] = "萨利维丝",
        ["Description"] = "暮光之锤在黑暗深渊里为萨利维丝准备了供她一人独居的私人住所，不断为她送上新鲜的祭品。邪教徒希望她的仪式和研究能形成魔法防护，使他们能尽情崇拜生活在深渊深处的可怕多头蛇，且无需担心自己会成为他的下一顿美餐。",
        },
        [579] = {
        ["Title"] = "黑色阔步者",
        ["Description"] = "外域的泥沼游荡者是徘徊在野外的高个子生物，以遇到的任何小型生物为食。在赞加沼泽的孢子人当中流传着与其中一位掠食者有关的传说，它的名字叫黑色阔步者，是个极为凶残的野兽。作为所有沼泽游荡者的巢母，据说她从幽暗沼泽深处的老巢中出来，就是为了满足自己想吃美味孢子人的口腹之欲。",
        },
        [616] = {
        ["Title"] = "噬魂者",
        ["Description"] = "布隆亚姆在灵魂洪炉中从事的黑暗产业可以生产出饱受折磨、支离破碎的灵魂。为了阻止这些灵魂逃走，并将这些亡魂的价值发挥到极致，巫妖王创造出了噬魂者。噬魂者会追击并吞噬那些游荡的灵魂，并将它们在自己可怕的身体里拆解。被吞噬的灵魂会因痛苦和煎熬而陷入疯狂，但噬魂者的欲望却永不满足。",
        },
        [744] = {
        ["Title"] = "刀锋领主塔亚克",
        ["Description"] = "塔亚克以耐心和智慧著称。在战斗中，他善于识破对手的弱点，静待良机、一招致命。塔亚克的剑法无人能及，而他也十分乐意将自己的战斗技巧传授给年轻的螳螂妖战士。",
        },
        [565] = {
